package com.nobodyhub.leetcode;

import org.junit.Test;

import static junit.framework.TestCase.assertEquals;

/**
 * @author yan_h
 * @since 2018/7/24
 */
public class LongestPalindromicSubstringTest {
    @Test
    public void test() {
        LongestPalindromicSubstring solution = new LongestPalindromicSubstring();
        assertEquals("abcba", solution.longestPalindrome("abcba"));
        assertEquals("bab", solution.longestPalindrome("babad"));
        assertEquals("bb", solution.longestPalindrome("cbbd"));
        assertEquals("a", solution.longestPalindrome("a"));
        assertEquals("bb", solution.longestPalindrome("bb"));
        assertEquals("", solution.longestPalindrome(""));
        assertEquals("a", solution.longestPalindrome("abcda"));
    }

    @Test
    public void test1() {
        LongestPalindromicSubstring solution = new LongestPalindromicSubstring();
        assertEquals("abcba", solution.longestPalindrome1("abcba"));
        assertEquals("bab", solution.longestPalindrome1("babad"));
        assertEquals("bb", solution.longestPalindrome1("cbbd"));
        assertEquals("a", solution.longestPalindrome1("a"));
        assertEquals("bb", solution.longestPalindrome1("bb"));
        assertEquals("",
                solution.longestPalindrome1(""));
        assertEquals("a", solution.longestPalindrome1("abcda"));
    }
}